export function mockTealium() {
  const tealiumMock = jest.fn();
  window.utag = {
    link: tealiumMock
  };
  jest.useFakeTimers();
  return tealiumMock;
}
export function unmockTealium() {
  jest.resetAllMocks();
  jest.useRealTimers();
}
export function waitForAnalytics() {
  jest.advanceTimersToNextTimer();
  new Promise(resolve => setTimeout(resolve, 0));
}
export function testAnalytics(testName, fn) {
  test(testName, async () => {
    const tealiumMock = mockTealium();
    const returnVal = await fn({
      tealiumMock,
      waitForAnalytics
    });
    unmockTealium();
    return returnVal;
  });
}
testAnalytics.skip = (testName, fn) => {
  test.skip(testName, () => fn({}));
};